import React from 'react';

const ProductListItem = (props) => {

    const product = props.product;

    // const check = (id) => {
    //     props.check(id);
    // }

    // const uncheck = (id) => {
    //     props.uncheck(id);
    // }

    const onCheck = (e) => {
        if (e.target.checked === true) {
            props.check(e.target.value);
        } else {
            props.uncheck(e.target.value);
        }
    }

    return (
        <div>
            {product && <div>
                <input className='' value={product.id} onChange={onCheck} type="checkbox"/>
                <p className=''>{ product.sku }</p>
                <p className=''>{ product.product_name }</p>
                <p className=''>{ product.price } $</p>
                { product.size && <p className="about_product">Size: { product.size } MB</p> }
                { product.weight && <p className="about_product">Weight: { product.weight }KG</p> }
                { product.height && <p className="about_product">Dimension: { product.height }x{product.width}x{product.length}</p> }
            </div>}
        </div>
    );
};

export default ProductListItem;
