import { React, useState } from "react";
import axios from 'axios';
import { Link } from "react-router-dom";

export function ProductAdd() {

    const [disabledBtn, setDisabledBtn] = useState(false);


    const submitForm = (e) => {
        e.preventDefault();
        setDisabledBtn(true);
    }
    return (
        <div>
            <form>
                <div>
                    <h3>Product Add</h3>
                    <div>
                        {!disabledBtn && <button type='submit'>Save</button>}
                        {disabledBtn && <button disabled>Saving...</button>}
                        {!disabledBtn && <Link to={'/'}><button>Cancel</button></Link>}
                        {disabledBtn && <button disabled>Cancel</button>}
                    </div>
                </div>
                <div>Form</div>
            </form>
        </div>
    );
};
